mapper.select:
select f.f_num as f_num, f.f_title as f_title, f.f_content as f_content, f.f_days as f_days, f.f_enddate as f_enddate, f.f_target as f_target, f.f_totalmoney as f_totalmoney, f.f_rate as f_rate, f.f_status as f_status, f.f_reward as f_reward, f.f_price as f_price, f.f_bname as f_bname, f.f_bnum as f_bnum, f.f_regdate as f_regdate, f.member_m_num as m_num, i.i_num as i_num, i.i_name as filename, i.i_uuid as uuid, i.i_type as i_type from ( select * from image where (funding_f_num, i_num) in ( select r.* from (select funding_f_num, min(i_num) as i_num from image where i_type = 1 group by funding_f_num) as r) ) as i inner join funding f on f.f_num = i.funding_f_num where 1 and f.f_status in (3,4,5)
and f.f_title like concat('%', #{searchName} ,'%')
and f.f_status = #{searchStatus}
order by
f.f_enddate asc,
f.f_status asc, f.f_num desc limit #{pageStart}, #{amount}

select f.f_num as f_num, f.f_title as f_title, f.f_content as f_content, f.f_days as f_days, f.f_enddate as f_enddate, f.f_target as f_target, f.f_totalmoney as f_totalmoney, f.f_rate as f_rate, f.f_status as f_status, f.f_reward as f_reward, f.f_price as f_price, f.f_bname as f_bname, f.f_bnum as f_bnum, f.f_regdate as f_regdate, f.member_m_num as m_num from funding f where f.f_status in (3,4,5)
and f.f_title like concat('%', #{searchName} ,'%')
and f.f_status = #{searchStatus}
order by f.f_num desc limit #{pageStart}, #{amount}

select f.f_num as f_num, f.f_title as f_title, f.f_days as f_days, f.f_status as f_status, f.f_bname as f_bname, (select m.m_id from member m where m.m_num = f.member_m_num) as m_id, f.f_regdate as f_regdate from funding f where f.f_status in (1,2)
and f.f_title like concat('%', #{searchName} ,'%')
and f.f_status = #{searchStatus}
order by f.f_num desc limit #{pageStart}, #{amount}

update funding
set f_title = #{f_title},
	f_content = #{f_content}
where f_num = #{f_num}

select count(*) as total from funding where f_status in (3,4,5)
and f_title like concat('%', #{searchName} ,'%')
and f_status = #{searchStatus}

select count(*) as total from funding where f_status in (1,2)
and f_title like concat('%', #{searchName} ,'%')
and f_status = #{searchStatus}

mapper.update:
update funding
set f_status = 3
where f_num = #{f_num}

update funding
set f_status = 2
where f_num = #{f_num}


update funding
set f_status = 5
where f_num = #{f_num}


update funding
set f_status = 4
where f_num = #{f_num}
